package bucles;

public class BucleFor {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int numero = 20;
		//Mostrar todos los números por debajo de la variable numero
		
		
		
		//for(inicialización;condición de continuidad;incremento){ ... }
		for(int i =1;i<=numero;i=i+2) {
			System.out.print(i + " ");
		}
				
	
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
